.rain, .snow {
    width: 8px;
    height: 14px;
    background: cyan;
    left: 0;
    top: -4px;
    display: inline-block;
    animation: rain 11.5s;
}

.rain:nth-child(2n+0) {
    width: 6px;
    height: 10px;
    margin-left: -1.7px;
    background-position: -10px -6px;
    animation: rain 13.5s;
}

.rain:nth-child(3n+0) {
    animation: rain 12s;
}

.drops {
    width: 146px;
    position: relative;
    margin: 0 auto;
    padding: 0;
    height: 250px;
    top: -150px;
    z-index: 0;
    padding-left: 8px
}

.floor {
    position: absolute;
    top: 146px
}

.fdrop {
    position: absolute
}

.snow {
    width: 8px!important;
    height: 8px!important;
    border-radius: 50%;
    background: #e6f5fb;
    left: -5px;
    top: -4px;
    animation: rain linear 6s infinite;
}

.snow:nth-child(2n+1) {
    width: 5px!important;
    height: 5px!important;
    margin-left: 2px;
    top: -7px;
    animation: rain linear 4.5s infinite;
    background: #e6f5fb;
}

.snow:nth-child(4n+0) {
    width: 9px!important;
    height: 9px!important;
    margin-left: 2px;
    animation: rain linear 8s infinite;
    background: #fff;
}

.weather {
    position: absolute;
    top: 0;
    width: 300px;
    height: 180px
}

@keyframes rain {
    0% {
        transform: translate3d(0, 0, 0.001px)
    }
    80% {
        transform: translate3d(0, 530px, 0.001px)
    }
    90% {
        transform: translate3d(0, 540px, 0.001px)
    }
    100% {
        transform: translate3d(0, 550px, 0.001px)
    }
}
